from typing import List, Union
from accessor import (
    getValue,
    setValue
)

def on_text_1_change(path: List[Union[str, int]], value):
    setValue(['text_2'], value)
    return

def on_text_2_change(path: List[Union[str, int]], value):
    setValue(['text_1'], value)
    return


